const body=document.body,sidebar=document.querySelector(".sidebar"),menuButton=document.querySelector(".header__menu"),closeButton=document.querySelector(".sidebar__exit");menuButton.addEventListener("click",()=>{window.innerWidth<1024?sidebar.classList.add("show"):(sidebar.classList.toggle("big-show"),body.style.marginLeft=sidebar.classList.contains("big-show")?"0":"300px")});closeButton.addEventListener("click",()=>{sidebar.classList.remove("show")});sidebarMenus=document.querySelectorAll(".sidebar__menu button");arrows=document.querySelectorAll(".fa-angle-right");sidebarMenus.forEach(n=>{n.addEventListener("click",()=>{const t=n.nextElementSibling,i=n.querySelector(".fa-angle-right");t&&t.classList.contains("sidebar__submenu")&&(t.classList.toggle("show"),i.style.transform=t.classList.contains("show")?"rotate(90deg)":"rotate(0deg)")})});