const itemQty=document.getElementById("itemQty"),hiddenQty=document.getElementById("hiddenQty"),decrease=document.getElementById("btnDecrease"),increase=document.getElementById("btnIncrease");let qty=1;increase.addEventListener("click",()=>{qty++,itemQty.textContent=qty,hiddenQty.value=qty});decrease.addEventListener("click",()=>{qty<=1?(itemQty.textContent=1,hiddenQty.value=1):(qty--,itemQty.textContent=qty,hiddenQty.value=qty)});const itemRating=document.getElementById("itemRating"),rating=document.getElementById("rating"),ratingButtons=document.querySelectorAll('input[name="rating"]');ratingButtons.forEach(n=>{n.addEventListener("change",()=>{itemRating.textContent=n.value,rating.value=n.value,console.log(rating.value)})});