const itemQty=document.getElementById("itemQty"),decrease=document.getElementById("btnDecrease"),increase=document.getElementById("btnIncrease");increase.addEventListener("click",()=>{let n=parseInt(itemQty.value,10)||1;itemQty.value=n+1});decrease.addEventListener("click",()=>{let n=parseInt(itemQty.value,10)||1;n>1&&(itemQty.value=n-1)});itemQty.addEventListener("input",()=>{let n=parseInt(itemQty.value,10);(isNaN(n)||n<1)&&(itemQty.value=1)});const itemRating=document.getElementById("itemRating"),rating=document.getElementById("rating"),ratingButtons=document.querySelectorAll('input[name="rating"]');ratingButtons.forEach(n=>{n.addEventListener("change",()=>{itemRating.textContent=n.value,rating.value=n.value,console.log(rating.value)})});