function updateCartSummary(){let n=0;document.querySelectorAll(".bookTotalPrice").forEach(t=>{n+=parseFloat(t.textContent.replace("₱","").trim())});document.getElementById("subTotal").textContent=`₱${n.toFixed(2)}`;document.getElementById("estimatedTotal").textContent=`₱${(n+85+25.23).toFixed(2)}`}function updateSubtotal(){let n=0;const t=85,i=25.23;bookTotalPrices.forEach(t=>{const i=parseFloat(t.textContent.replace("₱","").replace(",",""));n+=i});subTotal.textContent=`₱${n.toFixed(2)}`;estimatedTotal.textContent=`₱${(n+t+i).toFixed(2)}`;formTotalPrice.value=(n+t+i).toFixed(2)}document.querySelectorAll(".cart__item-remove").forEach(n=>{n.addEventListener("click",function(){var n=this.getAttribute("data-cart-id");document.getElementById("cartItemId").value=n;document.getElementById("removeForm").submit()})});const cartQuantities=document.querySelectorAll(".cartQuantity"),btnDecreases=document.querySelectorAll(".btnDecrease"),btnIncreases=document.querySelectorAll(".btnIncrease"),bookPrices=document.querySelectorAll(".bookPrice"),bookTotalPrices=document.querySelectorAll(".bookTotalPrice"),subTotal=document.getElementById("subTotal"),estimatedTotal=document.getElementById("estimatedTotal"),formBookQuantities=document.querySelectorAll(".order-book-quantity"),formTotalPrice=document.getElementById("orderTotalPrice");cartQuantities.forEach((n,t)=>{let i=parseInt(n.textContent),r=parseFloat(bookPrices[t].textContent.replace("₱","").replace(",","")),u=bookTotalPrices[t],f=formBookQuantities[t];btnDecreases[t].addEventListener("click",()=>{i>1&&(i-=1,n.textContent=i,f.value=i,u.textContent=`₱${(i*r).toFixed(2)}`,updateSubtotal())});btnIncreases[t].addEventListener("click",()=>{i+=1,n.textContent=i,f.value=i,u.textContent=`₱${(i*r).toFixed(2)}`,updateSubtotal()})});