const hamburger=document.querySelector(".fa-bars"),subHeader=document.querySelector(".header__sub");hamburger.addEventListener("click",()=>{subHeader.style.display=subHeader.style.display==="none"?"block":"none"});const headerCategoryPopup=document.getElementById("headerCategoryPopup"),headerCategory=document.getElementById("headerCategory"),categoryCaret=document.getElementById("categoryCaret");headerCategory.addEventListener("click",()=>{headerCategoryPopup.classList.toggle("show"),categoryCaret.style.transform=headerCategoryPopup.classList.contains("show")?"rotate(180deg)":"rotate(0deg)"});const headerSetting=document.getElementById("headerSetting"),profilePic=document.getElementById("profilePic");profilePic.addEventListener("click",()=>{headerSetting.classList.toggle("show")});